  `fabulous` int(11) DEFAULT '0',
  `looknum` int(11) DEFAULT '0',
  `category` int(1) DEFAULT NULL,
  `sharenum` int(11) DEFAULT '0',
  `abstractremark` varchar(255) DEFAULT NULL,
  `stick` tinyint(1) DEFAULT NULL,
  `display` int(1) DEFAULT NULL,
