DROP TABLE IF EXISTS `notice`;
CREATE TABLE `notice` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `title` varchar(256) DEFAULT NULL COMMENT '标题',
  `content` text COMMENT '内容',
  `isDisplay` bit(1) DEFAULT NULL COMMENT '是否显示，默认是true(显示)',
  `createTime` datetime DEFAULT NULL COMMENT '创建时间',
  `category` int(6) DEFAULT '0',
  `status` int(6) DEFAULT '0',
  `publicTime` datetime DEFAULT NULL,
  `lastModifyTime` datetime DEFAULT NULL,
  `display` int(11) NOT NULL DEFAULT '0',
  `img` varchar(256) DEFAULT NULL,
  `userId` int(10) NOT NULL DEFAULT '0',
  `person` tinyint(1) NOT NULL DEFAULT '0',
  `platsource` int(1) DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `idx_userId` (`userId`),
  KEY `idx_person` (`person`),
  KEY `index_notice_display` (`display`),
  KEY `index_notice_status` (`status`),
  KEY `index_notice_category` (`category`),
  FULLTEXT KEY `index_notice_title` (`title`)
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8;

