-- ----------------------------
-- Table structure for plat_event
-- ----------------------------
DROP TABLE IF EXISTS `plat_event`;
CREATE TABLE `plat_event` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(256) NOT NULL,
  `content` text,
  `edate` date NOT NULL,
  `createTime` datetime DEFAULT NULL,
  `status` tinyint(4) NOT NULL DEFAULT '1',
  `platsource` int(1) DEFAULT '0',
  `display` int(1) DEFAULT '1',
  PRIMARY KEY (`id`),
  KEY `index_platevent_status` (`status`),
  KEY `index_platevent_display` (`display`),
  FULLTEXT KEY `index_platevent_title` (`title`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of plat_event
-- ----------------------------
