-- ----------------------------
-- Table structure for so_message
-- ----------------------------
DROP TABLE IF EXISTS `so_message`;
CREATE TABLE `so_message` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `message` longtext CHARACTER SET utf8 COLLATE utf8_bin COMMENT '评论内容',
  `created_time` datetime DEFAULT NULL COMMENT '创建的时间',
  `parent_id` bigint(20) DEFAULT NULL COMMENT '父类id',
  `likes` int(11) DEFAULT NULL COMMENT '顶的次数',
  `agent` varchar(256) CHARACTER SET utf8 DEFAULT NULL COMMENT '浏览器信息',
  `ip` varchar(64) CHARACTER SET utf8 DEFAULT NULL COMMENT 'Ip',
  `iplocation` varchar(128) CHARACTER SET utf8 DEFAULT NULL COMMENT 'ip物理地址',
  `author_id` bigint(20) DEFAULT NULL COMMENT '评论者id',
  `pkey` varchar(256) CHARACTER SET utf8 DEFAULT NULL COMMENT 'key',
  `referer` varchar(256) CHARACTER SET utf8 DEFAULT NULL COMMENT '来源Url',
  `pids` varchar(256) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '子父类id串',
  `level` int(2) DEFAULT NULL COMMENT '层级',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=66 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

