DROP TABLE IF EXISTS `so_message_author`;
CREATE TABLE `so_message_author` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '用户id',
  `open_id` varchar(64) CHARACTER SET utf8 DEFAULT NULL COMMENT '用户的第三方登录ID',
  `open_type` int(1) DEFAULT NULL COMMENT '用户登录的第三方类型，预留',
  `avatar_url` varchar(256) CHARACTER SET utf8 DEFAULT NULL COMMENT '用户头像',
  `url` varchar(256) CHARACTER SET utf8 DEFAULT NULL COMMENT '用户点击跳转链接',
  `nickname` varchar(64) CHARACTER SET utf8 DEFAULT NULL COMMENT '用户名称',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `email` varchar(126) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '邮箱',
  `sucurity` varchar(32) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '安全码',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

