-- ----------------------------
-- Table structure for sys_setting
-- ----------------------------
DROP TABLE IF EXISTS `sys_setting`;
CREATE TABLE `sys_setting` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `current_version` varchar(20) DEFAULT NULL,
  `author` varchar(50) DEFAULT NULL,
  `domain_url` varchar(50) DEFAULT NULL,
  `server_config` varchar(255) DEFAULT NULL,
  `database_version` varchar(50) DEFAULT NULL,
  `max_file` varchar(20) DEFAULT NULL,
  `createtime` datetime DEFAULT NULL,
  `lastModifyTime` datetime DEFAULT NULL,
  `domainName` varchar(50) DEFAULT NULL,
  `icpcard` varchar(50) DEFAULT NULL,
  `description` varchar(255) DEFAULT NULL,
  `keywords` varchar(50) DEFAULT NULL,
  `copyright` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of sys_setting
-- ----------------------------
