-- ----------------------------
-- Table structure for user_log
-- ----------------------------
DROP TABLE IF EXISTS `user_log`;
CREATE TABLE `user_log` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '日志id',
  `userId` int(10) unsigned DEFAULT NULL COMMENT '操作用户id',
  `logType` smallint(5) unsigned NOT NULL COMMENT '日志类型id',
  `userName` varchar(19) DEFAULT NULL COMMENT '用户名称',
  `operModule` varchar(50) DEFAULT NULL COMMENT '操作模块',
  `operType` smallint(5) unsigned DEFAULT NULL COMMENT '0=新增,\n            1=修改,\n            2=删除,\n            3=异常\n            ',
  `operTime` datetime DEFAULT NULL COMMENT '操作时间',
  `remark` varchar(4000) DEFAULT NULL COMMENT '说明',
  `host` varchar(50) DEFAULT NULL COMMENT '操作IP地址',
  PRIMARY KEY (`id`),
  UNIQUE KEY `index_userlog_id` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=119 DEFAULT CHARSET=utf8;

