mapper.select:
select id,username,usercode,gender,phone,create_time as createTime,last_login_time as lastLoginTime,sourceimg,locked from sys_user s


    or s.username like CONCAT('%', #{filter}, '%')


    or s.usercode like CONCAT('%', #{filter}, '%')


     and s.last_login_time >= #{starttime} 


     and s.last_login_time <= #{endtime} 


order by s.last_login_time desc
